import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {

  const [time, useTime] = useState(0)  
  const [runnung, setRunning] = useState(false)

  return (
    <>
    <h1>Stopwatch</h1>
    <div>
    <span>{("0" + Math.floor((time/60000)%60))}:</span>
    <span>{("0" + Math.floor((time/1000)%60))}</span>
    <span>{("0"+ ((time/10)%100))}</span>
    </div>
    <div>
      <button>Start</button>
      <button>Stop</button>
      <button>Restart</button>
    </div>
    </>
  );
}

export default App;
